import pyglet as pgwindow = pg.window.Window(800, 600, visible=False)label = pg.text.Label('Welcome to Hnefatafl!',                          font_name='Times New Roman',                          font_size=36,                          x=window.width//2, y=window.height//2,                          anchor_x='center', anchor_y='center')window.set_visible()# programmeticly draw the gameboard # for the concrete placement of the pieces get the images from the subclasses# on click pass it on the the gameBoard@window.eventdef on_draw():    window.clear()    label.draw()if __name__ == '__main__':      pg.app.run()